Page._content:
.member-tearch-bg {
  background: url(http://image.kbiao.me/17-11-20/79431241.jpg) center center no-repeat #f3f5f7;
  background-size: cover;
  position: relative;
  text-align: center;
  color: #fff;
  padding: 30px 50px;
}

.member-tearch-avator {
  width: 96px;
  height: 96px;
  border-radius: 50%;
}

.member-tearch-info {
  margin: 16px 0;
}

.member-tearch-info span{
  padding: 0 16px;
}

.member-student-online {
  margin: 8px -8px;
  display: grid;
  font-size: 12px;
  grid-template-columns: 1fr 1fr 1fr;
}
@media only screen and (max-width: 479px) {

}
@media screen and (max-width: 559px), screen and (max-height: 479px) {
  .member-student-online {
   grid-template-columns: 1fr;
  }
}

.student-online-item {
  margin: 8px;
}

.student-online-item-header {
  border: 1px solid #e9e9e9;
  padding: 16px;
  display: flex;
  background-color: #f1f3f5;
}

.student-online-item-body {
  border: 1px solid #e9e9e9;
  padding: 8px 16px;
  margin-top: -1px;
  display: -webkit-box;
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 3;
  overflow: hidden;
  height: 84px;
}

.student-online-item-body:hover{
  border: 1px solid #e9e9e9;
  padding: 8px 16px;
  margin-top: -1px;
  min-height: 84px;
  height: 100%;
  cursor: pointer;
  display: block;
}

.student-online-avator {
  width: 60px;
  height: 60px !important;
}

.student-online-info {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  margin-left: 8px;
}

.student-online-info p {
  margin: 0;
  line-height: initial;
}

.student-online-info p:nth-child(1) {
  font-weight: 600;
}

.student-online-info p span{
  margin-left: 8px;
}


.member-outline {
  position: relative;
  z-index: 1010;
  margin-left: 16px;
  margin-bottom: 60px;
}

.member-outline::after {
  content: " ";
  position: absolute;
  top: 20px;
  left: 0;
  margin-left: -2px;
  width: 4px;
  height: 100%;
  background: #f5f5f5;
  z-index: -1;
}

.member-outline-year {
  position: relative;
  margin: 36px 0
}

.member-outline-year h2{
  margin:1.1em 20px;
  font-size: 16px;
}

.member-ouline-student p{
  margin:1.1em 20px;
  font-size: 12px;
}

.member-outline-year::before {
  content: " ";
  position: absolute;
  left: 0;
  top: 50%;
  margin-left: -4px;
  margin-top: -4px;
  width: 8px;
  height: 8px;
  background: #bbb;
  border-radius: 50%;
}

.member-ouline-student {
  position: relative;
  transition-duration: 0.2s;
  transition-timing-function: ease-in-out;
  transition-delay: 0s;
  transition-property: border;
  border-bottom: 1px dashed #ccc;
}

.member-ouline-student::before {
  content: " ";
  position: absolute;
  left: 0;
  top: 12px;
  width: 6px;
  height: 6px;
  margin-left: -4px;
  background: #bbb;
  border-radius: 50%;
  border: 1px solid #fff;
  transition-duration: 0.2s;
  transition-timing-function: ease-in-out;
  transition-delay: 0s;
  transition-property: background;
}

